import React, { useState } from "react";
import { useEffect } from "react";
import Navbar from "./components/Navbar";
import "./BuyPage.css";
import { initializeApp } from "firebase/app";
import { getDatabase, ref as databaseRef, child, get } from "firebase/database";
import { NavLink } from "react-router-dom";
import i from "./components/img/card.png";
import { data } from "jquery";
import { render } from "@testing-library/react";

const firebaseConfig = {
  apiKey: "AIzaSyCMM56fnkSA1P5p4b6plQso_3001MpEEYI",
  authDomain: "kgpsellex.firebaseapp.com",
  projectId: "kgpsellex",
  storageBucket: "kgpsellex.appspot.com",
  messagingSenderId: "750261842834",
  appId: "1:750261842834:web:309fb0919f637e0932074b",
  measurementId: "G-ZTC4HQ2M0V",
};

// Initialize Firebase
initializeApp(firebaseConfig);

function BuyPage() {
  const dbRef = databaseRef(getDatabase());
  const [dataObj, setDataObj] = useState({});

  const fetchData = () => {
    get(child(dbRef, `items/`))
      .then((snapshot) => {
        if (snapshot.exists()) {
          const data = snapshot.val();
          setDataObj(data);
        } else {
          alert("No data to display!");
        }
      })
      .catch((error) => {
        alert("Some error occured! Please try again later.");
      });
  };
  useEffect(() => {
    fetchData();
    console.log(dataObj);
  }, []);
  return (
    <>
      <Navbar />
      {(() => {
        for (let index = 0; index < 4; index++) {
            console.log(index);
          return(
            <div className="container box-shadow-card mb-4 mx-auto">
              <div className="row custom-flex">
                <div className="col-md-6 custom-flex">
                  <div className="d-flex justify-content-center m-2">
                    <img src={i} className=" product-img my-4" alt="..." />
                  </div>

                  <div className="m-2">
                    <h6>Seller</h6>
                    <p className="">{}</p>
                    <h6>Roll Number</h6>
                    <p className="">Roll number: </p>
                    <h6>Hall</h6>
                    <p className="">Hall: </p>
                    <h6>Email Address</h6>
                    <p className="">Email: </p>
                    <h6>Phone Number</h6>
                    <p className="">Phone Number: </p>
                    <h6>Product type</h6>
                    <p className="">Product type: </p>
                    <h6>Product Description</h6>
                    <p className="card-text">Product Description: </p>
                    <h6>Selling Price(INR)</h6>
                    <p className="">Price: </p>

                    <div className="d-flex flex-row">
                      <NavLink href="#" className="btn-get-started mx-2 my-3">
                        OPEN
                      </NavLink>
                      <NavLink
                        to="/email"
                        className="btn-get-started mx-2 my-3"
                      >
                        CONTACT
                      </NavLink>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          );
        }
      })()}
    </>
  );
}

export default BuyPage;
