import React from "react";
import { useEffect } from "react";
import Navbar from "./components/Navbar";
import "./BuyPage.css";
import { initializeApp } from "firebase/app";
import { getDatabase, ref as databaseRef, child, get } from "firebase/database";
import { NavLink } from "react-router-dom";
import i from "./components/img/card.png";

const firebaseConfig = {
  apiKey: "AIzaSyCMM56fnkSA1P5p4b6plQso_3001MpEEYI",
  authDomain: "kgpsellex.firebaseapp.com",
  projectId: "kgpsellex",
  storageBucket: "kgpsellex.appspot.com",
  messagingSenderId: "750261842834",
  appId: "1:750261842834:web:309fb0919f637e0932074b",
  measurementId: "G-ZTC4HQ2M0V",
};

// Initialize Firebase
initializeApp(firebaseConfig);

function BuyPage() {
  const dbRef = databaseRef(getDatabase());

  const fetchData = () => {
    get(child(dbRef, `items/`))
      .then((snapshot) => {
        if (snapshot.exists()) {
          const data = snapshot.val();
        } else {
          alert("No data to display!");
        }
      })
      .catch((error) => {
        alert("Some error occured! Please try again later.");
      });
  };
  useEffect(() => {
    fetchData();
    console.log("Hello");
  }, []);
  return (
    <>
      <Navbar />
      <div className="container contact_div box-shadow-card mx-auto">
        <div className="row">
          <div className="col-md-12 d-flex justify-content-between">
            <img src={i} className="card-img-top product-img col-md-6" alt="..." />
            <div className="card-body">
              <h5 className="card-title">NAME</h5>
              <p className="card-text">ROLL NO</p>
              <p className="card-text">CONTACT NO</p>
              <p className="card-text">PRODUCT NAME</p>
              <p className="trxt-end">PRICE RANGE</p>

              <p className="card-text">PRODUCT DESCRIPTION</p>
              <NavLink href="#" className="btn-get-started">
                OPEN
              </NavLink>
              <NavLink href="#" className="btn-get-started">
                MESSAGE
              </NavLink>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default BuyPage;
