import React, { useState } from "react";
import "./Alert.css";

function Alert() {
  const alertDiv = document.getElementById("alert-div");
  const [dis, setDis] = useState("none");
  setTimeout(() => {
    setDis("block");
  }, 4000);


  return (
    <div class="alert alert-warning d-flex justify-content-between align-items-center" style={{display: dis}}>
  PLease keep checking your spam folder of your mail folder. The buyer might try to contact you!
</div>
  );
}

export default Alert;
