import React, { useState } from "react";
import "./Alert.css";

function Alert() {
  const alertDiv = document.getElementById("alert-div");
  const [dis, setDis] = useState("none");
  setTimeout(() => {
    setDis("block");
  }, 1500);

  return (
    <div
      class="alert alert-warning alert-dismissible fade show"
      id="alert-div"
    //   style={{display: {dis}}}
    >
      <strong>PLease!</strong> You should keep checking your spam folder in your
      mail app for any updates.
      <button
        onClick={()=>{setDis("none")}}
        class="btn-close"
        
      ></button>
    </div>
  );
}

export default Alert;
